interface Message {
    message: string;
    isUser: boolean;
    sources?: string[];
  }

  const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;

  const ChatWindow = ({
    messages,
    formatSource,
  }: {
    messages: Message[];
    formatSource: (source: string) => string;
  }) => (
    <div className="flex-grow bg-gray-700 shadow overflow-hidden sm:rounded-lg border-b border-gray-600 p-4">
      {messages.map((msg, idx) => (
        <div
          key={idx}
          className={`p-3 my-3 rounded-lg text-white ml-auto ${msg.isUser ? 'bg-gray-800' : 'bg-gray-900'}`}
        >
          {msg.message}
          {!msg.isUser && Array.isArray(msg.sources) && msg.sources.length > 0 &&(
            <div className="text-xs mt-4 border-t pt-2 border-gray-600">
              {msg.sources.map((src, i) => (
                <div key={i}>
                  <a
                    target="_blank"
                    download
                    rel="noreferrer"
                    href={`${BACKEND_URL}/rag/static/${encodeURI(formatSource(src))}`}
                  >
                    {formatSource(src)}
                  </a>
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );

  export default ChatWindow;
