interface Props {
    inputValue: string;
    onInputChange: (e: React.ChangeEvent<HTMLTextAreaElement>) => void;
    onSend: () => void;
    onKeyPress: (e: React.KeyboardEvent<HTMLTextAreaElement>) => void;
  }

  const ChatInput = ({ inputValue, onInputChange, onSend, onKeyPress }: Props) => (
    <div className="p-4 bg-gray-800">
      <textarea
        className="form-textarea w-full p-2 border rounded text-white bg-gray-900 border-gray-600 resize-none"
        placeholder="Enter your message here..."
        value={inputValue}
        onChange={onInputChange}
        onKeyUp={onKeyPress}
      />
      <button
        className="mt-2 bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
        onClick={onSend}
      >
        Send
      </button>
    </div>
  );

  export default ChatInput;
